select * from (
select assessment_id assessmentId, "assessment" modelName, assessment_stage assessmentStage, assessment_type assessmentType, assessment_item assessmentItem, assessment_result assessmentResult, "" doctorAdviceResult, "" doctorAdviceRisk, assessment_score assessmentScore, create_dt createDt from tb_vte_assessment t where IFNULL(t.is_enable,'0') = 1 and t.patient_hospit_id = #{patientHospitId}
UNION ALL select doctor_advice_id assessmentId, "advice" modelName, "" assessmentStage, '6' assessmentType, "10" assessmentItem, "" assessmentResult, doctor_advice_result doctorAdviceResult, doctor_advice_risk doctorAdviceRisk, NULL assessmentScore, create_dt createDt from tb_vte_doctor_advice t where IFNULL(t.is_enable,'0') = 1 and t.patient_hospit_id = #{patientHospitId} ) tt order by tt.createDt desc limit #{start},#{limit}

select assessment_id assessmentId, "assessment" modelName, assessment_stage assessmentStage, assessment_type assessmentType, assessment_item assessmentItem, assessment_result assessmentResult, "" doctorAdviceResult, "" doctorAdviceRisk, assessment_score assessmentScore, create_dt createDt from tb_vte_assessment t where IFNULL(t.is_enable,'0') = 1 and t.patient_hospit_id = #{patientHospitId}
and t.assessment_stage = #{assessmentStage}
and t.assessment_type = #{assessmentType}
and t.assessment_item = #{assessmentItem}
order by createDt desc limit #{start},#{limit}

	select  count(*)  from tb_vte_assessment t
		where 
			IFNULL(t.is_enable,'0') = 1  and t.patient_hospit_id  =  #{patientHospitId}

       		and t.assessment_stage  =  #{assessmentStage}


       		and t.assessment_type  =  #{assessmentType}


       		and t.assessment_item  =  #{assessmentItem}

select  count(*)  from tb_vte_doctor_advice t
	where 
		IFNULL(t.is_enable,'0') = 1  and t.patient_hospit_id  =  #{patientHospitId}

		select tt.assessmentTypeCode,
		tt.assessmentTypeExplain,
		tt.assessmentTypeValue
		 from (

            SELECT 
             #{c.assessmentTypeCode} assessmentTypeCode,
             #{c.assessmentType} assessmentTypeExplain,
             COUNT(*) assessmentTypeValue
             FROM tb_vte_assessment 
             WHERE IFNULL(is_enable,'0') = 1 
             and  patient_hospit_id = #{c.patientHospitId} 
             and assessment_type =  #{c.assessmentTypeCode}

		) tt

select * from (
select assessment_id assessmentId, "assessment" modelName, assessment_stage assessmentStage, assessment_type assessmentType, assessment_item assessmentItem, assessment_result assessmentResult, "" doctorAdviceResult, "" doctorAdviceRisk, assessment_score assessmentScore, DATE_FORMAT(create_dt,'%Y-%m-%d') createDt from tb_vte_assessment t where IFNULL(t.is_enable,'0') = 1 and t.patient_hospit_id = #{patientHospitId}
UNION ALL select doctor_advice_id assessmentId, "advice" modelName, "" assessmentStage, '6' assessmentType, "10" assessmentItem, "" assessmentResult, doctor_advice_result doctorAdviceResult, doctor_advice_risk doctorAdviceRisk, "" assessmentScore, DATE_FORMAT(create_dt,'%Y-%m-%d') createDt from tb_vte_doctor_advice t where IFNULL(t.is_enable,'0') = 1 and t.patient_hospit_id = #{patientHospitId} ) tt order by tt.createDt desc

select assessment_id assessmentId, "assessment" modelName, assessment_stage assessmentStage, assessment_type assessmentType, assessment_item assessmentItem, assessment_result assessmentResult, "" doctorAdviceResult, "" doctorAdviceRisk, assessment_score assessmentScore, DATE_FORMAT(create_dt,'%Y-%m-%d') createDt from tb_vte_assessment t where IFNULL(t.is_enable,'0') = 1 and t.patient_hospit_id = #{patientHospitId}
and t.assessment_stage = #{assessmentStage}
and t.assessment_type = #{assessmentType}
and t.assessment_item = #{assessmentItem}
order by createDt desc
