mapper.update:
update tb_vte_patient_hospit_info
patient_code = #{patientCode}, patient_in_hospital = #{patientInHospital}, patient_out_hospital = #{patientOutHospital}, patient_number = #{patientNumber}, patient_age = #{patientAge}, patient_native_place = #{patientNativePlace}, patient_job = #{patientJob}, patient_marital = #{patientMarital}, patient_phone_number = #{patientPhoneNumber}, patient_department = #{patientDepartment}, patient_area = #{patientArea}, patient_bed = #{patientBed}, patient_doctor = #{patientDoctor}, patient_is_risk = #{patientIsRisk}, patient_caprini_grade = #{patientCapriniGrade}, patient_padua_grade = #{patientPaduaGrade}, patient_syn_grade = #{patientSynGrade}, patient_last_risk_user = #{patientLastRiskUser}, patient_last_risk_type = #{patientLastRiskType}, patient_last_risk_date = #{patientLastRiskDate}, patient_from = #{patientFrom}, is_enable = #{isEnable}, create_by = #{createBy}, create_dt = #{createDt}, update_by = #{updateBy}, update_dt = #{updateDt},
where patient_hospit_id = #{patientHospitId}

