select t.patient_hospit_id patientHospitId, t.patient_department patientDepartment, d.department_id departmentId, d.department_code departmentCode from tb_vte_patient_hospit_info t inner join tb_vte_department d on d.department_name = t.patient_department where IFNULL(t.is_enable,'0') = 1 and IFNULL(d.is_enable,'0') = 1
and t.patient_hospit_id = #{patientHospitId}
limit 0,1

		SELECT
			count(*) count,
			tvphi.patient_department patientDepartment
		FROM
			tb_vte_patient_hospit_info tvphi

			LEFT JOIN tb_vte_department tvd on tvphi.patient_department=tvd.department_name

		WHERE
			IFNULL(tvphi.is_enable, '0') = 1


       		and tvphi.patient_department=#{patientDepartment}



       			and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))


       			and date_format(tvphi.patient_in_hospital,'%Y')=#{date}




       			and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1


       			and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}




       			and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK(
					date_sub(curdate(), INTERVAL 7 DAY),
					1
				)


       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS}
       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}



       		and IFNULL(tvd.is_enable, '0') = 1
			and tvd.department_qualitycontrol=#{departmentQualitycontrol}


			GROUP BY tvphi.patient_department

SELECT count(*) count, tvphi.patient_department patientDepartment FROM tb_vte_patient_hospit_info tvphi LEFT JOIN tb_vte_doctor_advice tvda on tvphi.patient_hospit_id = tvda.patient_hospit_id
LEFT JOIN tb_vte_department tvd on tvphi.patient_department=tvd.department_name
where IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvda.is_enable, '0') = 1 and tvda.doctor_advice_is_last = 1
and tvphi.patient_department=#{patientDepartment}
and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))
and date_format(tvphi.patient_in_hospital,'%Y')=#{date}
and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1
and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}
and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK( date_sub(curdate(), INTERVAL 7 DAY), 1 )
and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS} and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}
and (tvda.doctor_advice_result=#{doctorAdviceResult1} or tvda.doctor_advice_result=#{doctorAdviceResult2})
and IFNULL(tvd.is_enable, '0') = 1 and tvd.department_qualitycontrol=#{departmentQualitycontrol}
GROUP BY tvphi.patient_department

			SELECT
				count(*) count,
				tvphi.patient_department patientDepartment
			FROM
				tb_vte_patient_hospit_info tvphi
			LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id

				LEFT JOIN tb_vte_department tvd on tvphi.patient_department=tvd.department_name

			WHERE
				 IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1
				 and tvaa.assessment_type_is_last = 1
				 and tvaa.assessment_item_is_last = 1
				 AND (
							tvaa.assessment_item = #{assessmentItem1}
							OR tvaa.assessment_item = #{assessmentItem2}
						)

       		and TIMESTAMPDIFF(SECOND,tvphi.patient_in_hospital,tvaa.create_dt) <= 86400


	       		and tvphi.patient_department=#{patientDepartment}



       			and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))


       			and date_format(tvphi.patient_in_hospital,'%Y')=#{date}




       			and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1


       			and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}




       			and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK(
					date_sub(curdate(), INTERVAL 7 DAY),
					1
				)


       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS}
       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}



	       		and IFNULL(tvd.is_enable, '0') = 1
				and tvd.department_qualitycontrol=#{departmentQualitycontrol}


			GROUP BY tvphi.patient_department

			SELECT
				count(*) count,
				tvphi.patient_department patientDepartment
			FROM
				tb_vte_patient_hospit_info tvphi
			LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id
			WHERE
				IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1
				AND tvaa.assessment_type_is_last = 1
				AND tvaa.assessment_type = #{assessmentType}
				AND tvaa.assessment_item = #{assessmentItem}
				AND tvaa.assessment_result = #{assessmentResult}


       			and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))


       			and date_format(tvphi.patient_in_hospital,'%Y')=#{date}




       			and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1


       			and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}




       			and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK(
					date_sub(curdate(), INTERVAL 7 DAY),
					1
				)


       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS}
       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}


			GROUP BY
				tvphi.patient_department
			ORDER BY
				count DESC

SELECT count(*) count, tvphi.patient_department patientDepartment FROM tb_vte_patient_hospit_info tvphi LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id WHERE IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1 AND tvaa.assessment_type_is_last = 1 AND tvaa.assessment_type = #{assessmentType} AND ((tvaa.assessment_item = #{assessmentItem1} and (tvaa.assessment_result = #{assessmentResult1} or tvaa.assessment_result = #{assessmentResult2})) or (tvaa.assessment_item = #{assessmentItem2} and tvaa.assessment_result = #{assessmentResult2}))
and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))
and date_format(tvphi.patient_in_hospital,'%Y')=#{date}
and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1
and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}
and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK( date_sub(curdate(), INTERVAL 7 DAY), 1 )
and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS} and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}
GROUP BY tvphi.patient_department ORDER BY count DESC

SELECT count(*) count, tvphi.patient_department patientDepartment FROM tb_vte_patient_hospit_info tvphi LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id LEFT JOIN tb_vte_doctor_advice tvda on tvphi.patient_hospit_id = tvda.patient_hospit_id where IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1 and IFNULL(tvda.is_enable, '0') = 1 AND tvaa.assessment_type_is_last = 1 AND tvaa.assessment_type = #{assessmentType} AND tvda.doctor_advice_is_last = 1 AND ((tvaa.assessment_item = #{assessmentItem1} and (tvaa.assessment_result = #{assessmentResult1} or tvaa.assessment_result = #{assessmentResult2})) or (tvaa.assessment_item = #{assessmentItem2} and tvaa.assessment_result = #{assessmentResult2}))
and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))
and date_format(tvphi.patient_in_hospital,'%Y')=#{date}
and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1
