SELECT count(*) count, tvphi.patient_department patientDepartment FROM tb_vte_patient_hospit_info tvphi LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id WHERE IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1 AND tvaa.assessment_type_is_last = 1 AND tvaa.assessment_type = #{assessmentType} AND tvaa.assessment_result = #{assessmentResult}
and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))
and date_format(tvphi.patient_in_hospital,'%Y')=#{date}
and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1
