			SELECT
				count(*) count,
				tvphi.patient_department patientDepartment
			FROM
				tb_vte_patient_hospit_info tvphi 
			LEFT JOIN tb_vte_doctor_advice tvda on tvphi.patient_hospit_id = tvda.patient_hospit_id
			where IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvda.is_enable, '0') = 1
			AND tvda.doctor_advice_is_last = 1


       			and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))


       			and date_format(tvphi.patient_in_hospital,'%Y')=#{date}




       			and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1


       			and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}




       			and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK(
					date_sub(curdate(), INTERVAL 7 DAY),
					1
				)


       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS}
       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}



			and tvda.doctor_advice_result=#{doctorAdviceResult}


			and (tvda.doctor_advice_result=#{doctorAdviceResult1} or tvda.doctor_advice_result=#{doctorAdviceResult2})


	       		and tvphi.patient_department=#{patientDepartment}

			GROUP BY tvphi.patient_department

SELECT count(*) count, tso.org_type orgType FROM tb_vte_patient_hospit_info tvphi LEFT JOIN tb_vte_doctor_advice tvda ON tvphi.patient_hospit_id = tvda.patient_hospit_id LEFT JOIN tb_vte_department tvd on tvphi.patient_department=tvd.department_name LEFT JOIN tb_system_org tso on tvd.department_code=tso.org_code WHERE IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvd.is_enable, '0') = 1 and IFNULL(tvda.is_enable, '0') = 1 and IFNULL(tso.is_enable, '0') = 1 AND tvda.doctor_advice_is_last = 1 and tvd.department_qualitycontrol=#{departmentQualitycontrol}
and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))
and date_format(tvphi.patient_in_hospital,'%Y')=#{date}
and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1
and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}
and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK( date_sub(curdate(), INTERVAL 7 DAY), 1 )
and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS} and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}
and (tso.org_type=#{orgType1} or tso.org_type=#{orgType2})
and tvda.doctor_advice_result=#{doctorAdviceResult}
and (tvda.doctor_advice_result=#{doctorAdviceResult1} or tvda.doctor_advice_result=#{doctorAdviceResult2})
and tvphi.patient_department=#{patientDepartment}
GROUP BY tso.org_type

SELECT count(*) count, tvphi.patient_department patientDepartment FROM tb_vte_patient_hospit_info tvphi LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id WHERE IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1 AND tvaa.assessment_type_is_last = 1
and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))
and date_format(tvphi.patient_in_hospital,'%Y')=#{date}
and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1
and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}
and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK( date_sub(curdate(), INTERVAL 7 DAY), 1 )
and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS} and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}
AND tvaa.assessment_item = #{assessmentItem} and tvaa.assessment_result = #{assessmentResult} GROUP BY tvphi.patient_department ORDER BY count DESC

			SELECT
				count(*) count,
				tvphi.patient_department patientDepartment
			FROM
				tb_vte_patient_hospit_info tvphi
			LEFT JOIN tb_vte_assessment tvaa ON tvphi.patient_hospit_id = tvaa.patient_hospit_id
			WHERE
				IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1
				AND tvaa.assessment_type_is_last = 1
				AND tvaa.assessment_type = #{assessmentType}
				AND tvaa.assessment_item = #{assessmentItem}
				AND tvaa.assessment_result = #{assessmentResult}


       			and year(tvphi.patient_in_hospital)=year(date_sub(now(),interval 1 year))


       			and date_format(tvphi.patient_in_hospital,'%Y')=#{date}




       			and PERIOD_DIFF( date_format( now( ) , '%Y%m' ) , date_format(tvphi.patient_in_hospital, '%Y%m' ) ) =1


       			and date_format(tvphi.patient_in_hospital,'%Y-%m')=#{date}




       			and YEARWEEK(tvphi.patient_in_hospital, 1) = YEARWEEK(
					date_sub(curdate(), INTERVAL 7 DAY),
					1
				)


       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') >= #{dateS}
       			and date_format(tvphi.patient_in_hospital,'%Y-%m-%d') <= #{dateE}


			GROUP BY
				tvphi.patient_department
			ORDER BY
				count DESC
