mapper.update:
update tb_vte_patient
patient_code = #{patientCode}, patient_name = #{patientName}, patient_sex = #{patientSex}, patient_nation = #{patientNation}, patient_id_code = #{patientIdCode}, is_enable = #{isEnable}, create_by = #{createBy}, create_dt = #{createDt}, update_by = #{updateBy}, update_dt = #{updateDt},
where patient_id = #{patientId}

mapper.select:
   	select 
		        patient_id  patientId,
		        patient_code  patientCode,
		        patient_name  patientName,
		        patient_sex  patientSex,
		        patient_nation  patientNation,
		        patient_id_code  patientIdCode,
		        is_enable  isEnable,
		        create_by  createBy,
		        create_dt  createDt,
		        update_by  updateBy,
				update_dt   updateDt
       from tb_vte_patient t
       where 
       IFNULL(t.is_enable,'0') = 1

       		and t.patient_id  =  #{patientId}


       		and t.patient_code  =  #{patientCode}


       		and t.patient_name  =  #{patientName}


       		and t.patient_sex  =  #{patientSex}


       		and t.patient_nation  =  #{patientNation}


       		and t.patient_id_code  =  #{patientIdCode}


       		and t.is_enable  =  #{isEnable}


       		and t.create_by  =  #{createBy}


       		and t.create_dt  =  #{createDt}


       		and t.update_by  =  #{updateBy}


       		and t.update_dt  =  #{updateDt}


		order by $pageSort$ 

		limit #{start},#{limit}

