select t.patient_id patientId, t.patient_code patientCode, t.patient_name patientName, t.patient_sex patientSex, t.patient_nation patientNation, t.patient_id_code patientIdCode, ht.patient_hospit_id patientHospitId, ht.patient_in_hospital patientInHospital, ht.patient_out_hospital patientOutHospital, ht.patient_number patientNumber, ht.patient_age patientAge, ht.patient_native_place patientNativePlace, ht.patient_job patientJob, ht.patient_marital patientMarital, ht.patient_phone_number patientPhoneNumber, ht.patient_department patientDepartment, ht.patient_area patientArea, ht.patient_bed patientBed, ht.patient_doctor patientDoctor, ht.patient_is_risk patientIsRisk, ht.patient_caprini_grade patientCapriniGrade, ht.patient_padua_grade patientPaduaGrade, ht.patient_syn_grade patientSynGrade, ht.patient_last_risk_user patientLastRiskUser, ht.patient_last_risk_date patientLastRiskDate, ht.patient_from patientFrom, ht.create_dt createDt from tb_vte_patient t inner join tb_vte_patient_hospit_info ht on ht.patient_code = t.patient_code where IFNULL(t.is_enable,'0') = 1 and IFNULL(ht.is_enable,'0') = 1
and t.patient_id = #{patientId}
and t.patient_code = #{patientCode}
and t.patient_name like '%${patientName}%'
and t.patient_sex = #{patientSex}
and t.patient_nation like '%${patientNation}%'
and t.patient_id_code = #{patientIdCode}
and ht.patient_hospit_id = #{patientHospitId}
and ht.patient_in_hospital = #{patientInHospital}
and date_format(ht.patient_in_hospital,'%Y-%m-%d') >= date_format(#{patientInHospitalStart},'%Y-%m-%d')
and date_format( ht.patient_in_hospital,'%Y-%m-%d') <= date_format(#{patientInHospitalEnd},'%Y-%m-%d')
and ht.patient_out_hospital = #{patientOutHospital}
and date_format(ht.patient_out_hospital,'%Y-%m-%d') >= date_format(#{patientOutHospitalStart},'%Y-%m-%d')
and date_format( ht.patient_out_hospital,'%Y-%m-%d') <= date_format(#{patientOutHospitalEnd},'%Y-%m-%d')
and ht.patient_number = #{patientNumber}
and ht.patient_age = #{patientAge}
and ht.patient_native_place like '%${patientNativePlace}%'
and ht.patient_job like '%${patientJob}%'
and ht.patient_marital like '%${patientMarital}%'
and ht.patient_phone_number = #{patientPhoneNumber}
and ht.patient_department like '%${patientDepartment}%'
and ht.patient_area like '%${patientArea}%'
and ht.patient_bed like '%${patientBed}%'
and ht.patient_doctor like '%${patientDoctor}%'
and ht.patient_is_risk = #{patientIsRisk}
and ht.patient_caprini_grade = #{patientCapriniGrade}
and ht.patient_padua_grade = #{patientPaduaGrade}
and ht.patient_syn_grade = #{patientSynGrade}
and ht.patient_last_risk_user like '%${patientLastRiskUser}%'
and ht.patient_last_risk_date = #{patientLastRiskDate}
and exists (select t2.org_id from tb_vte_department t1 inner join tb_system_org t2 on t2.org_code = t1. department_code where IFNULL(t1.is_enable,'0') = 1 and IFNULL(t2.is_enable,'0') = 1 and t1.department_name = ht.patient_department and t2.org_name = #{departmentName} )
order by patientInHospital desc, createDt desc
limit #{start},#{limit}

	select  count(*)   from tb_vte_patient t inner join tb_vte_patient_hospit_info ht on ht.patient_code = t.patient_code
		where 
			IFNULL(t.is_enable,'0') = 1
  		 and   IFNULL(ht.is_enable,'0') = 1

       		and t.patient_id  =  #{patientId}


       		and t.patient_code  =  #{patientCode}


       		and t.patient_name  like  '%${patientName}%'


       		and t.patient_sex  =  #{patientSex}


       		and t.patient_nation  like  '%${patientNation}%'


       		and t.patient_id_code  =  #{patientIdCode}


       		and ht.patient_hospit_id  =  #{patientHospitId}


       		and ht.patient_in_hospital  =  #{patientInHospital}


       		and date_format(ht.patient_in_hospital,'%Y-%m-%d')  >= date_format(#{patientInHospitalStart},'%Y-%m-%d')


       		and date_format( ht.patient_in_hospital,'%Y-%m-%d')  <=  date_format(#{patientInHospitalEnd},'%Y-%m-%d')


       		and ht.patient_out_hospital  =  #{patientOutHospital}


       		and date_format(ht.patient_out_hospital,'%Y-%m-%d')  >= date_format(#{patientOutHospitalStart},'%Y-%m-%d')


       		and date_format( ht.patient_out_hospital,'%Y-%m-%d') <= date_format(#{patientOutHospitalEnd},'%Y-%m-%d')


       		and ht.patient_number  =  #{patientNumber}


       		and ht.patient_age  =  #{patientAge}


       		and ht.patient_native_place  like  '%${patientNativePlace}%' 


       		and ht.patient_job  like  '%${patientJob}%'  


       		and ht.patient_marital  like  '%${patientMarital}%' 


       		and ht.patient_phone_number  =  #{patientPhoneNumber}


       		and ht.patient_department  like  '%${patientDepartment}%'  


       		and ht.patient_area   like  '%${patientArea}%'  


       		and ht.patient_bed   like  '%${patientBed}%'   


       		and ht.patient_doctor  like  '%${patientDoctor}%'  


       		and ht.patient_is_risk  =  #{patientIsRisk}


       		and ht.patient_caprini_grade  =  #{patientCapriniGrade}


       		and ht.patient_padua_grade  =  #{patientPaduaGrade}


       		and ht.patient_syn_grade  =  #{patientSynGrade}


       		and ht.patient_last_risk_user  like  '%${patientLastRiskUser}%'  


       		and ht.patient_last_risk_date  =  #{patientLastRiskDate}


       		and exists (select t2.org_id from tb_vte_department t1 
       		inner join
       		tb_system_org t2 
       		on  t2.org_code = t1. department_code 
       	    where   IFNULL(t1.is_enable,'0') = 1 and   IFNULL(t2.is_enable,'0') = 1
       		and t1.department_name = ht.patient_department 
       		and t2.org_name =  #{departmentName} )

select tvp.patient_id patientId, tvp.patient_code patientCode, tvp.patient_name patientName, tvp.patient_sex patientSex, tvp.patient_nation patientNation, tvp.patient_id_code patientIdCode, tvphi.patient_age patientAge, tvphi.patient_hospit_id patientHospitId, tvphi.patient_last_risk_date patientLastRiskDate, tvphi.patient_number patientNumber, tvphi.patient_area patientArea, tvphi.patient_bed patientBed, tvaa.assessment_item assessmentItem, tvaa.assessment_result assessmentResult from tb_vte_patient tvp left join ( SELECT phi.* FROM ( SELECT patient_hospit_id, patient_code, patient_age, patient_last_risk_date, patient_out_hospital, patient_department, patient_number, patient_area, patient_bed, patient_last_risk_type, is_enable FROM tb_vte_patient_hospit_info WHERE IFNULL(is_enable, '0') = 1 ORDER BY patient_in_hospital DESC ) phi GROUP BY phi.patient_code ) tvphi on tvp.patient_code=tvphi.patient_code left join tb_vte_assessment tvaa on tvphi.patient_hospit_id=tvaa.patient_hospit_id
LEFT JOIN tb_vte_department tvd on tvphi.patient_department=tvd.department_name
where 1=1 and IFNULL(tvp.is_enable, '0') = 1 and IFNULL(tvphi.is_enable, '0') = 1 and IFNULL(tvaa.is_enable, '0') = 1 AND tvaa.assessment_type_is_last = 1 AND tvaa.assessment_type = #{assessmentType} AND ((tvaa.assessment_item = #{assessmentItem1} and (tvaa.assessment_result = #{assessmentResult1} or tvaa.assessment_result = #{assessmentResult2})) or (tvaa.assessment_item = #{assessmentItem2} and tvaa.assessment_result = #{assessmentResult2})) AND tvphi.patient_department=#{patientDepartment}
and DATE_SUB(CURDATE(), INTERVAL 1 MONTH) <= date(tvphi.patient_last_risk_date)
and DATE_SUB(CURDATE(), INTERVAL 3 MONTH) <= date(tvphi.patient_last_risk_date)
and tvphi.patient_out_hospital is not null
and tvphi.patient_out_hospital is null
and IFNULL(tvd.is_enable, '0') = 1 and tvd.department_code=#{departmentCode}
order by tvaa.assessment_result desc limit #{start},#{limit}

	   SELECT
			ifnull(max(assessment_id),0) assessmentId
		FROM
			tb_vte_assessment 
		WHERE
			IFNULL(is_enable, '0') = 1 and patient_hospit_id =#{patientHospitId}

       		AND assessment_type = #{assessmentType}


       		AND assessment_item = #{assessmentItem}

       	ORDER BY create_dt desc LIMIT 0,1
